mapper.update:
UPDATE qna_board 
SET qna_title = #{qna_title}, qna_content = #{qna_content}, qna_date = CURRENT_TIMESTAMP
WHERE qna_seq = #{qna_seq}

UPDATE qna_board 
SET answer_title = #{answer_title}, answer_content = #{answer_content} 
WHERE qna_seq = #{qna_seq}

UPDATE reply 
SET reply = #{reply}, replydate = CURRENT_TIMESTAMP
WHERE rno = #{rno}

UPDATE notify_board 
SET notify_title = #{notify_title}, notify_content = #{notify_content}, notify_date = CURRENT_TIMESTAMP
WHERE notify_seq = #{notify_seq}
